

hideAllMenu = function ( ){
	$('#menuA').hide();
	$('#menuB').hide();
	$('#menuC').hide();
	$('#menuLinkA').removeClass('selected');
	$('#menuLinkB').removeClass('selected');
	$('#menuLinkC').removeClass('selected');
}
function hideMenu(){
	var count = 0;
	var a=$(this).attr("rel");
	$("#"+a).mouseleave(function(){
		hideAllMenu();
	});
	$(this).mouseleave(function(){
		count++;
	});
	if (count == 2){
		hideAllMenu();
	}
}




