/*!
 * showMenu( menuid );
 *
 * Show the menu item on the page
 */	

showMenuA = function ( ){
	hideAllMenu();
	$('#menuLinkA').addClass('selected');
	$('#menuA').fadeIn('fast');
}

showMenuB = function ( ){
	hideAllMenu();
	$('#menuLinkB').addClass('selected');
	$('#menuB').fadeIn('fast');
}

showMenuC = function ( ){
	hideAllMenu();
	$('#menuLinkC').addClass('selected');
	$('#menuC').fadeIn('fast');
}

 $("#menuLinkA").hoverIntent({
		over: showMenuA,
		out: hideMenu,
		interval: 50,
		sensitivity: 3	
});	

$("#menuLinkB").hoverIntent({
	over: showMenuB,
	out: hideMenu,
		interval: 50,
		sensitivity: 3	
});	

$("#menuLinkC").hoverIntent({
	over: showMenuC,
	out: hideMenu,
		interval: 50,
		sensitivity: 3	
});


