closeMenus = function(){
	$('.selected').removeClass("selected");
	$('#menuA-holder,#menuB-holder,#menuC-holder').hide();
}
setOpenMenu = function(menuLetter){
	openMenu = menuLetter;
}
// show the search
$('.searchOpen').click(function(){
	$('.searchContainer').slideToggle(60);
});

